(182, 7, 3, 'Auction Creation Customer Notification Template', 12, '', '', 'New Auction available', '95%', '<p><img src=\"https://demo.perxclm.com/Email_body_images/1441968777_zvPbZ9dh_auction-banner.png\" alt=\"\" width=\"600\" height=\"150\" /></p>\r\n<p>Dear <strong>$First_name $Last_name</strong>,</p>\r\n<p><br />Congratulations ! You qualify for the&nbsp;<strong>$Auction_name</strong>&nbsp; Bidding.</p>\r\n<p>Participate and get a chance to Win amazing items!</p>\r\n<p>Click <a href=\"https://www.demo.perxclm.com/Company_7\">here</a>&nbsp;to manage your Peace Advantage loyalty account&nbsp;</p>\r\n<p>OR</p>\r\n<p>download our mobile app on your Android or Blackberry10 and above devices.</p>\r\n<p><strong>For further enquiries, please contact our call center on&nbsp;</strong><a style=\"text-decoration: none; color: #011441;\" href=\"tel:+2348176689760\">234-817-668-9760</a>&nbsp;or <strong>send an email</strong> to&nbsp;<a style=\"text-decoration: none; color: #011441;\" href=\"mailto:callcenter@flyairpeace.com\">callcenter@flyairpeace.com</a></p>\r\n<p>&nbsp;</p>\r\n<p>Thank you for choosing to fly with Air Peace</p>\r\n<p>&nbsp;</p>\r\n<p>Regards</p>\r\n<p>The Peace Advantage Loyalty Team</p>\r\n<p>&nbsp;</p>', '', '12px', 'Bookman Old Style', '#353535', '#F0F0F0', 'Powered by Loyalty Solutions Limited, Nigeria $Website', 1, 1, 0, 1, 665, '2015-09-11 10:09:12', 665, '2015-09-11 11:56:43', 8, 0, '', '', '', 0, 0, 0, 0),
(183, 9, 3, 'E-Voucher Redemption Template', 14, '', '', 'Your Redemption Details', '95%', '<p><img src=\"https://demo.perxclm.com/Email_body_images/1441968949_2jTktXVK_redemption-banner.png\" alt=\"\" width=\"600\" height=\"150\" /></p>\r\n<p>Dear $First_name <strong>$Last_name</strong>,&nbsp; <br /><br />You have successfully registered your intention to redeem an item. &nbsp;This is your voucher number $Voucher_no. Kindly call our dedicated customer service line&nbsp;<a style=\"text-decoration: none; color: #ca1830;\" href=\"tel:+2348176689759\">+234-817-668-9762</a>&nbsp;to speak with an agent</p>\r\n<p>Your Item and Order details are below table.<br /><br /><br /></p>\r\n<p>Remember you can track and manage your loyalty account from here or simply download our mobile app on your Android or BlackBerry10 and above devices.</p>\r\n<p>&nbsp;</p>\r\n<p>Thank you for choosing to fly with&nbsp;AirPeace</p>\r\n<p>&nbsp;</p>\r\n<p>Regards</p>\r\n<p>The Peace Advantage Loyalty Team</p>', '', '12px', 'Bookman Old Style', '#353535', '#FFF9F5', 'Powered by Loyalty Solutions Limited, Nigeria', 1, 1, 0, 1, 665, '2015-09-11 10:16:39', 665, '2015-09-11 11:55:20', 12, 0, '', '', '', 0, 0, 0, 0),
