(60, 57, 'Member Birthday Template', 'Member Birthday Template', 362, '', '', 'Wishing you for your Birthday....', '80%', '<h1 style=\"text-align: center;\"><em><strong>HAPPY</strong></em><br /><em><strong>BIRTHDAY</strong></em><br /><em><strong>TO YOU</strong></em><br /><br /><br /><em><strong>Hey, $First_name $Last_name. We heard it\"s your Birthday Today.</strong></em><br /><em><strong>So we wanted to send you a little something special.</strong></em><br /><br /><em><strong>Show us this email and get 200 points.</strong></em><br /><em><strong>It\"s your Birthday - you Deserve it.</strong></em></h1>\r\n<p>&nbsp;</p>\r\n<p style=\"text-align: center;\"><img src=\"https://demo.perxclm.com/Email_body_images/1432898180_aMJj8HaB_giftbox.png\" alt=\"\" width=\"182\" height=\"182\" />&nbsp;&nbsp;</p>', '', '12px', 'Arial Rounded MT Bold', '#FFFFFF', '#A8211E', 'Powered by Loyalty Solutions Limited, Nigeria', 1, 1, 1, 1, 1, '2015-05-29 08:42:16', 2, '2015-05-30 10:12:15'),
(61, 58, 'Member Anniversary Template', 'Member Anniversary Template', 363, '', '', 'Wishing you for your Anniversary....\r\n', '80%', '<h1 style=\"text-align: center;\"><em><strong>HAPPY</strong></em><br /><em><strong>ANNIVERSARY</strong></em><br /><em><strong>TO YOU</strong></em><br /><br /><br /><em><strong>Hey, $First_name $Last_name. We heard it\"s your Anniversary Today.</strong></em><br /><em><strong>So we wanted to send you a little something special.</strong></em><br /><br /><em><strong>Show us this email and get 200 points.</strong></em><br /><em><strong>It\"s your Anniversary - you Deserve it.</strong></em></h1>\r\n<p>&nbsp;</p>\r\n<h1 style=\"text-align: center;\"><em><strong>Have a Wonderful</strong></em></h1>\r\n<h1 style=\"text-align: center;\"><em><strong>day filled</strong></em></h1>\r\n<h1 style=\"text-align: center;\"><em><strong>with Love...</strong></em></h1>\r\n<p>&nbsp;</p>\r\n<p style=\"text-align: center;\"><em><strong><img src=\"https://demo.perxclm.com/Email_body_images/1432898791_KfomiqYB_weddinganniv.png\" alt=\"\" width=\"300\" height=\"300\" /> <br /></strong></em></p>', '', '12px', 'Arial Rounded MT Bold', '#FFFFFF', '#999999', 'Powered by Loyalty Solutions Limited, Nigeria', 1, 1, 1, 1, 1, '2015-05-29 09:00:55', 2, '2015-05-30 10:13:49'),
(62, 59, 'Membership Expiry Intimation Template', 'Membership Expiry Intimation Template', 382, '', '', 'About your Membership Expiry Notification', '60%', '<p>Dear <strong>$First_name $Last_name</strong>,</p>\r\n<p>&nbsp;</p>\r\n<p>Your Membership is going to Expire on <strong>$Expiry_date</strong>. So to continue with our Loyalty Programme please kindly contact our Customer call center.</p>\r\n<p><br />Please login&nbsp;<strong>$Website</strong> to check your details</p>\r\n<p style=\"text-align: left;\">OR</p>\r\n<p>download the APK from <strong>$Website</strong></p>', '', '12px', 'Roboto,sans-serif', '#FFBE5C', '#3DB1FF', 'Powered by Loyalty Solutions Limited, Nigeria', 1, 0, 0, 1, 1, '2015-07-07 10:23:07', 1, '2015-07-07 10:23:07'),
(63, 60, 'Membership Expired Template', 'Membership Expired Template', 382, '', '', 'About Your Membership Expired.', '60%', '<p>Dear <strong>$First_name $Last_name</strong>,</p>\r\n<p>&nbsp;</p>\r\n<p>Your Membership is Expired on <strong>$Expiry_date</strong>. So please kindly contact our Customer call center.</p>\r\n<p><br />Please login&nbsp;<strong>$Website</strong> to check your details</p>\r\n<p style=\"text-align: left;\">OR</p>\r\n<p>download the APK from <strong>$Website</strong></p>', '', '12px', 'Roboto,sans-serif', '#FFBE5C', '#3DB1FF', 'Powered by Loyalty Solutions Limited', 1, 0, 0, 1, 1, '2015-07-07 10:24:28', 1, '2015-07-07 10:24:28'),
(64, 61, 'Membership Renew Notification Template', 'Membership Renew Notification Template', 388, '', '', 'About your Renewal of Membership Validity', '80%', '<p>Dear <strong>$First_name $Last_name</strong>,</p>\r\n<p>&nbsp;</p>\r\n<p>Your Membership Validity is now Renewed. You can now do transactions in our Loyalty Programme.</p>\r\n<p>New membership validity is :-&nbsp; <strong>$Membership_validity.</strong></p>\r\n<p>&nbsp;</p>\r\n<p>Please login <strong>$Website</strong> to check your details</p>\r\n<p>OR</p>\r\n<p>download the APK from <strong>$Website</strong></p>', '', '12px', 'Roboto,sans-serif', '#FFB259', '#2E96FF', 'Powered by Loyalty Solutions Limited', 1, 0, 0, 1, 1, '2015-07-07 10:25:33', 1, '2015-07-07 10:25:33'),
(65, 62, 'Points Forfeited 5days Template', '5 days Forfeiture Reminder Template', 17, '', '', '5 days Forfeiture Reminder', '40%', '<p>Dear $First_name,</p>\r\n<p>This is a reminder of Points which will be forfeited 5&nbsp;days from now.</p>\r\n<p>&nbsp;</p>\r\n<p>Your current Balance is $Current_balance.</p>\r\n<p>Your $Forfeit_points will be forfeited.</p>\r\n<p>&nbsp;</p>\r\n<p>Regards,</p>\r\n<p>Loyalty team</p>', '', '12px', 'Trebuchet MS', '', '#b8db9b', '', 0, 0, 0, 1, 2, '2015-07-17 12:05:55', 2, '2015-07-17 12:05:55'),
(66, 63, 'Points Expiry 5days Template', '5  Days Reminder of Expiry of Points', 18, '', '', '5  Days Reminder of Expiry of Points', '40%', '<p>Dear $First_name,</p>\r\n<p>This is a reminder of below points which will get expired 5&nbsp;days from now.</p>\r\n<p>&nbsp;</p>\r\n<p>Your current Balance is $Current_balance.</p>\r\n<p>&nbsp;</p>\r\n<p>So please Redeem soon to avoid Expiry of Points</p>\r\n<p>&nbsp;</p>\r\n<p>Regards,</p>\r\n<p>Loyalty team</p>', '', '12px', 'Trebuchet MS', '', '#a6e6f0', '', 0, 0, 0, 1, 2, '2015-07-17 12:09:04', 2, '2015-07-17 12:09:04'),
(67, 14, 'Item Minumum Reorder Level Email/SMS template', 'Threshold Balance reached', 15, '', '', 'Threshold Balance of $Item_name reached', '40%', '<p>Dear $First_name $Last_name, <br />&nbsp;<br />$Item_name Current balance has reached the threshold </p>\r\n<p>Below are the Details: <br /><br />Branch Name : $Branch_name <br /><br />Item Name :&nbsp;$Item_name &nbsp; <br /><br />Item Threshold : $Threshold_balance<br /><br />Item Current Balance : $Current_item_balance<br /><br /><br />Kind Regards, <br />The Loyalty Team</p>', '', '12px', '', '', '', '', 0, 0, 0, 1, 2, '2015-07-31 06:47:07', 2, '2015-07-31 06:47:07'),
(68, 64, 'LBS Campaign Notification Template', 'LBS Campaign Notification', 21, '', '', 'Campaign Details', '95%', '<p>Dear <strong>$First_name $Last_name</strong>,</p>\r\n<p>&nbsp;</p>\r\n<p>We are happy to inform you that, we have running a Campaign. So come and shop with us to get Rewards.</p>\r\n<p>Campaign details are below.</p>\r\n<p>&nbsp;</p>\r\n<p>Please login&nbsp;<strong>$Website</strong> to check your details</p>\r\n<p style=\"text-align: left;\">OR</p>\r\n<p>download the APK from <strong>$Website</strong></p>', '', '12px', 'Comic Sans MS', '#000203', '#B8C6FF', 'Powered by Loyalty Solutions Limited, Nigeria', 1, 1, 1, 1, 2, '2015-09-30 09:07:10', 2, '2015-09-30 09:07:10'),
(69, 65, 'Customer Online Enrollment Template', 'Customer (Parent Member) Online Enrollment Template', 9, '', '', 'About Enrollment in our $Loyalty_program_name', '95%', '<p>Dear <strong>$First_name $Last_name</strong>,</p>\r\n<p><br />Congratulations! You are now enrolled in our <strong>$Company_name </strong>Company.</p>\r\n<p><br />Please login&nbsp;<span style=\"text-decoration: underline;\"><strong>$Website</strong></span> to check your details</p>\r\n<p>OR</p>\r\n<p>download the APK from <span style=\"text-decoration: underline;\"><strong>$Website</strong></span></p>', '', '12px', 'Roboto,sans-serif', '#000203', '#b8db9b', 'Powered by Loyalty Solutions Limited, Nigeria', 1, 1, 1, 1, 2, '2015-10-06 08:56:24', 2, '2015-10-06 08:56:24'),
(70, 66, 'Family Member Online Enrollment Template', 'Family Member Online Enrollment Template', 9, '', '', 'About your Enrollment in $Loyalty_program_name', '80%', '<p>Dear <strong>$First_name $Last_name</strong>,</p>\r\n<p><br /><strong>Congratulations! </strong>You are now enrolled in our&nbsp;<strong>$Loyalty_program_name</strong> of&nbsp; our <strong>$Company_name </strong>Program.</p>\r\n<p>&nbsp;</p>\r\n<p>You are Enrolled under <strong>$Parent_first_name $Parent_last_name</strong> as Parent.</p>\r\n<p>&nbsp;<br />Please login&nbsp;<strong>$Website</strong> to check your details</p>\r\n<p style=\"text-align: left;\">OR</p>\r\n<p>download the APK from <strong>$Website</strong></p>', '', '12px', 'Roboto,sans-serif', '#000203', '#a6e6f0', 'Powered by Loyalty Solutions Limited, Nigeria', 1, 1, 1, 1, 2, '2015-10-06 09:00:31', 2, '2015-10-06 09:00:31');
