(71, 67, 'Monthly Transactions Detail Notification template', 'Monthly Transaction Notification', 403, '', '', 'Your Monthly Transactions', '80%', '<p>Dear $First_name,</p>\r\n<p>&nbsp;</p>\r\n<p>Below are the details of your transactions for the period $From_period to $To_period</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>', '', '12px', 'Calibri', '', '', '', 0, 0, 0, 1, 2, '2015-11-20 09:39:42', 2, '2015-11-20 09:39:42'),
(72, 68, 'Monthly Transactions Summmary Notification templat', 'Monthly Summary Transactions', 403, '', '', 'Your Monthly Transaction Summary', '80%', '<p>Dear $First_name,</p>\r\n<p>&nbsp;</p>\r\n<p>Your Transaction Summary for the period $From_period to $To_period</p>\r\n<p>&nbsp;</p>\r\n<p>Total Number of Transactions : $Total_no_of_transactions</p>\r\n<p>Total Transaction Amount : $Total_transaction_amount</p>\r\n<p>Total Points Gained : $Total_points_gained</p>\r\n<p>Total Points Used : $Total_points_used&nbsp;</p>\r\n<p>&nbsp;</p>', '', '12px', 'Calibri', '', '#fefffe', '', 0, 0, 0, 1, 2, '2015-11-20 09:41:46', 2, '2015-11-20 09:41:46'),
(73, 69, 'Qtrly Transaction Detail Notification Template', 'Quarterly Transaction Details', 403, '', '', 'Your Transaction Details for the Quarter', '80%', '<p>Dear $First_name,</p>\r\n<p>&nbsp;</p>\r\n<p>Below are the Transaction details for last Quarter viz Period $From_period to $To_period</p>\r\n<p>&nbsp;</p>', '', '12px', 'Arial', '', '#dadada', '', 0, 0, 0, 1, 2, '2015-11-20 09:45:24', 2, '2015-11-20 09:45:24'),
(74, 70, 'Qtrly Transaction Summary Notificaiton Template', 'Quarterly Transaction Summary ', 403, '', '', 'Your Transaction Summary for the Quarter', '80%', '<p>Dear $First_name,</p>\r\n<p>&nbsp;</p>\r\n<p>Please find below the Trasnaction summary for the Quarter . Period $From_period to $To_period</p>\r\n<p>&nbsp;</p>\r\n<p>Total No. of Transactions : $Total_no_of_transactions</p>\r\n<p>Total Transaction Amount : $Total_transaction_amount</p>\r\n<p>Total Points Gained : $Total_points_gained</p>\r\n<p>Total Points Used :$Total_points_used</p>\r\n<p>&nbsp;</p>\r\n<p>Regards,</p>', '', '12px', 'Arial', '', '#dadada', '', 0, 0, 0, 1, 2, '2015-11-20 09:48:21', 2, '2015-11-20 09:48:21'),
(75, 75, 'Game Winner Notification Template', 'Game Winner Notification Template', 429, '', '', 'Congratulation!! Game Winner...', '80%', '<p>Hello $First_name $Last_name,</p>\r\n<p>&nbsp;</p>\r\n<p>Heartly Congratulations...!!!</p>\r\n<p>&nbsp;</p>\r\n<p>The Game Competition is over now and You are the highest scorere for the Game $Game_name.</p>\r\n<p>&nbsp;</p>\r\n<p>So you are the Game Winner for the Game $Game_name.</p>\r\n<p>You got $Prize points/prize for this winning.</p>\r\n<p>&nbsp;</p>\r\n<p>Thank you.</p>\r\n<p>Regards LSL Team</p>', '', '12px', 'Arial Rounded MT Bold', '#2432FF', '#F6A3FF', 'Loyalty Solutions Nigeria', 0, 1, 1, 1, 2, '2016-01-14 10:59:50', 2, '2016-01-14 10:59:50'),
(76, 71, 'Customer Based Trigger Notification Template', 'Customer Tier Trigger Notification', 440, '', '', 'Exclusive Offer to our $Tier_name customers only!', '40%', '<p>Dear $First_name,</p>\r\n<p>&nbsp;</p>\r\n<p>You are our Loyal $Tier_name Customer and your current balance is $Current_balance.</p>\r\n<p>&nbsp;</p>\r\n<p>We are pleased to offer you the below Items which you can purchase from any of our outlets. This has&nbsp; been made exclusively to you.</p>\r\n<p>&nbsp;</p>\r\n<p>1. Sony Experia Smartphone</p>\r\n<p>&nbsp;</p>\r\n<p>2. Sony Digital LED TV</p>\r\n<p>&nbsp;</p>\r\n<p>3. Sony Earphone</p>\r\n<p>&nbsp;</p>\r\n<p>We thank you for your patronage. Purcashe the above Items and gain more points</p>\r\n<p>&nbsp;</p>\r\n<p>Regards,</p>\r\n<p>Loyalty Team</p>', '', '12px', 'Arial', '', '', '', 0, 0, 0, 1, 2, '2016-01-25 06:44:07', 2, '2016-01-25 06:44:07'),
(77, 72, 'Spend Campaign Based Trigger Notification Template', 'Spend Campaign Based Trigger Notification Template', 441, '', '', 'Spend More and Gain More!!!', '40%', '<p>Dear $First_name,</p>\r\n<p>&nbsp;</p>\r\n<p>Thank You for your continued patronage with us.</p>\r\n<p>&nbsp;</p>\r\n<p>If you purchase&nbsp;$Difference_value further you will easily qualify to gain points which you can Redeem at any of our outlets.</p>\r\n<p>&nbsp;</p>\r\n<p>Enjoy Purhcasing with us!</p>\r\n<p>&nbsp;</p>\r\n<p>Regards,<br />Loyalty Team</p>', '', '12px', 'Calibri', '', '#f9ee95', '', 0, 0, 0, 1, 2, '2016-01-25 07:06:32', 2, '2016-01-25 07:32:12'),
(78, 76, 'Cumulative Campaign Based Trigger Notification Tem', 'Cumulative Campaign Based Trigger Notification Template', 441, '', '', 'You will soon Qualify because of your Cumulative Spend with us!', '40%', '<p>Dear $First_name,</p>\r\n<p>&nbsp;</p>\r\n<p>Thank you for your continued patronage with us.</p>\r\n<p>&nbsp;</p>\r\n<p>With your last purchase , your cumulative purchase with us is&nbsp; $Cumulative_spend. If you purchase further&nbsp;$Difference_value you will qualify for loyalty points due to existing campaigns running acros all the outlets.</p>\r\n<p>So do purchase and gain more!!</p>\r\n<p>&nbsp;</p>\r\n<p>Regards,</p>\r\n<p>Loyalty Team</p>\r\n<p>&nbsp;</p>', '', '12px', 'Arial Rounded MT Bold', '', '#b8db9b', '', 0, 0, 0, 1, 2, '2016-01-25 07:11:46', 2, '2016-01-25 07:33:10'),
(79, 78, 'Transaction Type Count Campaign Based Trigger Noti', 'Transaction Type Count Campaign Based Trigger Notification Template', 441, '', '', 'More Number of Times you Purchase $Transaction_channel the more you GET!', '40%', '<p>Dear $First_name,</p>\r\n<p>&nbsp;</p>\r\n<p>Thank you for your patronage with us.</p>\r\n<p>&nbsp;</p>\r\n<p>We would like to let you know that if you purhase $Difference_value number of times from our $Transaction_channel then you will get further more points.</p>\r\n<p>&nbsp;</p>\r\n<p>Enjoy your purchasing!!</p>\r\n<p>&nbsp;</p>\r\n<p>Regards,<br />Loyalty Team</p>', '', '12px', 'Calibri', '', '#a6e6f0', '', 0, 0, 0, 1, 2, '2016-01-25 08:16:57', 2, '2016-01-25 08:16:57'),
(80, 77, 'Frequency Campaign Based Trigger Notification Temp', 'Frequency Campaign Based Trigger Notification Template', 441, '', '', 'Spend More Number of Times and Gain More', '40%', '<p>Dear $First_name,</p>\r\n<p>&nbsp;</p>\r\n<p>Thank you for your continued patronage with us.</p>\r\n<p>&nbsp;</p>\r\n<p>We have noticed that if you purchase $Difference_value times further you will be able to get some loyalty porints which you can redeem at any of our outlets.</p>\r\n<p>&nbsp;</p>\r\n<p>So go ahead and visit any of our outlets!</p>\r\n<p>&nbsp;</p>\r\n<p>Regards,</p>\r\n<p>Loyalty Team</p>', '', '12px', 'Calibri', '', '#FFABD8', '', 0, 0, 0, 1, 2, '2016-01-25 11:09:32', 2, '2016-01-25 11:09:32'),
(81, 73, 'Auction Based Trigger Notification Template', 'Auction Based Trigger Notification Template', 442, '', '', 'HURRY ...$Auction_name Auction Getting Over in next 7 days! ', '40%', '<p>Hello $First_name,</p>\r\n<p>&nbsp;</p>\r\n<p>Thank you for participating in auction $Auction_name.</p>\r\n<p>&nbsp;</p>\r\n<p>There are only&nbsp;$Days days of when the Auction end s. So BID further and be the Higest BIDDER and WIN exclusive Prize!</p>\r\n<p>&nbsp;</p>\r\n<p>Regards,</p>\r\n<p>LSL Team</p>', '', '12px', 'Arial Rounded MT Bold', '#000203', '#b8db9b', 'LAST 7 DAYS OF AUCTION', 0, 0, 0, 1, 2, '2016-01-27 10:42:02', 2, '2016-02-04 07:43:08'),
(82, 74, 'Game Based Trigger Notification Template', 'Game Based Trigger Notification Template', 443, '', '', '$Game_name Game Competition ending Soon', '40%', '<p>Dear $First_name,</p>\r\n<p>&nbsp;</p>\r\n<p>Thank you for playing $Game_name Game.</p>\r\n<p>&nbsp;</p>\r\n<p>In the next&nbsp;$Days days the competition for the above game is getting over. So try to complete all the levels and get Higest Score to WIN an exclusive prize.</p>\r\n<p>&nbsp;</p>\r\n<p>Thank you</p>\r\n<p>&nbsp;</p>\r\n<p>Regards,</p>\r\n<p>LSL Team</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>', '', '12px', 'Arial Rounded MT Bold', '#000203', '#dadada', '', 0, 0, 0, 1, 2, '2016-01-27 10:45:47', 2, '2016-02-04 07:44:00');
